import vue from 'vue';
import vuex from 'vuex';

vue.use(vuex);
const commentStore = new vuex.Store({
	state: {
		id: '',
		nickName: '匿名用户',
		allmessages: [{
			content: "你好",
			createDate: "2022-09-22",
			createTime: "2022-09-22 21:53:06",
			id: 1,
			name:'百年孤独',
			nickName: "匿名用户"
		}]
	},
	getters: {
		getMessages(state) {
			// console.log(state.id)
			console.log(state.allmessages)
			// console.log(state.allmessages.filter(p=>p.id==state.id&&p.nickName==state.nickName))
			return state.allmessages.filter(p => p.id == state.id && p.nickName == state.nickName);
		},
	},
	mutations: {
		setId(state, data) {
			// console.log("id",data)
			vue.set(state, "id", data.data)
		},
		messageAdd(state, data) {
			state.allmessages.push(data.data)
			// console.log(state.allmessages)
		}
	}

});

export default commentStore;
