export default {
  // props: ['visible'],
  data: function data() {
    return {
      labelPosition: 'right',
      showQrcode: true,
      username: '',
      password: ''
      /* qrcode:'' */

    };
  },
  methods: {
    clikeclose: function clikeclose() {
      this.$emit('update:visible', false);
    },
    checkLogin: function checkLogin() {
      this.$store.commit({
        type: "Login2Deal",
        data: {
          username: this.username,
          password: this.password
        }
      });

      if (this.$store.state.token == 1) {
        this.$message({
          message: "请输入用户名或密码！",
          type: "error",
          duration: 2000
        });
        return;
      } else if (this.$store.state.token == 2) {
        this.password = '', this.$message({
          message: "用户名或密码错误,请重新输入！",
          type: "error",
          duration: 2000
        });
        return;
      } else if (this.$store.state.token == 4) {
        this.username = '';
        this.password = '';
        this.$message({
          message: "账号异常，请稍后重试！",
          type: "error",
          duration: 2000
        });
        return;
      } else if (this.$store.state.token == 3) {
        this.username = '', this.password = '', this.$notify({
          title: "登录成功！",
          type: "success",
          duration: 2000
        });
        this.$emit("toMainPage", {
          flag: true
        }); // this.$emit('toMainPage', {
        // 	uname: this.uname,
        // 	upwds: this.upwds
        // });
        // this.$store.state.usernameShowOrNot = false;
        // this.$store.dispatch({
        //     type: "asyncFindUserById",
        //     user: {
        //         username: this.username
        //     },
        // });

        if (this.$store.state.radio == 0 || this.$store.state.radio == 1) {
          this.$router.push("/index");
          return;
        }

        if (this.$store.state.radio == 2) {
          this.$router.push("/");
        }
      }
    }
  }
};
