import bookStore from "./image/bookcenter.js";
export default {
  computed: {
    getBookType: function getBookType() {
      return bookStore.getters.getBookType;
    }
  },
  data: function data() {
    return {
      booktype: [],
      tempdata: '',
      addvalue: ''
    };
  },
  methods: {
    openBook: function openBook(item) {
      var h = this.$createElement;

      if (this.tempdata != '') {
        this.$notify({
          title: '提示',
          message: h('i', {
            style: 'color: red'
          }, '请先保存正在修改的值！')
        });
        return;
      }

      this.tempdata = item.name;
      item.flag = true;
    },
    closeBook: function closeBook(item) {
      var h = this.$createElement;

      if (this.getBookType.filter(function (p) {
        return p.name == item.name;
      }).length > 0) {
        this.$notify({
          title: '警告',
          message: h('i', {
            style: 'color: red'
          }, '名称重复或没有修改！')
        });
        item.name = this.tempdata;
      } else {
        bookStore.commit({
          type: 'editbooktype',
          data: item
        });
      }

      this.tempdata = '';
      item.flag = false;
    },
    addbooktype: function addbooktype() {
      var _this = this;

      var h = this.$createElement;

      if (this.addvalue == '') {
        this.$notify({
          title: '提示',
          message: h('i', {
            style: 'color: red'
          }, '类名不能为空！')
        });
        return;
      }

      if (this.getBookType.filter(function (p) {
        return p.name == _this.addvalue;
      }).length > 0) {
        this.$notify({
          title: '提示',
          message: h('i', {
            style: 'color: red'
          }, '分类已存在！')
        });
        return;
      } else {
        bookStore.commit({
          type: 'addbooktype',
          data: {
            id: Date.now(),
            name: this.addvalue,
            flag: false
          }
        });
        this.booktype = JSON.parse(JSON.stringify(this.getBookType));
      }

      this.$notify({
        title: '提示',
        message: h('i', {
          style: 'color: green'
        }, '添加成功！')
      });
      this.addvalue = '';
    }
  },
  mounted: function mounted() {
    this.booktype = JSON.parse(JSON.stringify(this.getBookType));
  }
};
